    public static List<List<Integer>> threeSum(int[] a) {
        Set<List<Integer>> result = new HashSet<>();
        Arrays.sort(a);
        for (int i = 0; i < a.length - 2; i++) {
            int l = i + 1;
            int r = a.length - 1;
            if (a[i] == a[r] && a[i] == 0) {
                result.add(Arrays.asList(0, 0, 0));
                break;
            }
            while (r > l) {
                int sum = a[i] + a[l] + a[r];
                if (sum == 0) {
                    List<Integer> zeroSums = Arrays.asList(a[i], a[l], a[r]);
                    result.add(zeroSums);
                    l++;
                    r--;
                } else if (sum > 0) {
                    r--;
                } else {
                    l++;
                }
            }
        }
        return new ArrayList<>(result);
    }
